import React, { useEffect, useState } from 'react'
import ProductItem from '../ProductItem'
import { useDispatch, useSelector } from 'react-redux'
import { RootState } from '../../../../store'
import { Product } from '../../../../types/product.type'
import axios from 'axios'
import { getProductList, setPage } from '../../../../product.reducer'
import $ from 'jquery'

export default function ProductList() {
    const dispatch = useDispatch();
    const {productList, page} = useSelector((state: RootState) => state.productReducer);
    const [isLoading, setIsLoading] = useState(false);
    const getProductionList = async () => {
        console.log(page);
        setIsLoading(true);
        try {
            const offset = (page - 1) * 20;
        const res = await axios.get(`https://dummyjson.com/products?limit=20&skip=${offset}`)
            // console.log(res)
            setIsLoading(false);
            if(res.status===200){
                dispatch(setPage(page + 1));
                dispatch(getProductList(res.data.products))
            }
        } catch (error) {
            setIsLoading(false);
        }
    }
    useEffect(()=>{
        getProductionList();
        onScroll();
    },[]);

    const onScroll = () => {
        $(window).scroll(function() {
            const a: any = $(window).scrollTop();
                const b: any = $(window).height();
                const c: any = $(document).height();
                if(a + b === c) {
                    getProductionList();
                }
        });
    }

    const handleScroll = () => {
        if (window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight || isLoading) {
          return;
        }
        getProductionList();
      };

      useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
      }, [isLoading]);


    return (
        <div className='text-center'>
            <div className="row">
                {productList.map((item, index) => {
                    return <ProductItem product={item} key={index} />
                })}
            </div>

        </div>
    )
}
