import React, { useEffect } from 'react'
import ProductItem from '../ProductItem'
import { useDispatch, useSelector } from 'react-redux'
import { RootState } from '../../../../store'
import { Product } from '../../../../types/product.type'
import axios from 'axios'
import { getProductList } from '../../../../product.reducer'

export default function ProductList() {
    const dispatch = useDispatch()
    useEffect(()=>{
        const productList =async () => {
            const res = await axios.get("https://dummyjson.com/products?limit=20")
            console.log(res)
            if(res.status==200){
              dispatch(getProductList(res.data))
            }
        }
        productList()
    },[])
    const productList = useSelector((state: RootState) => state.productReducer.productList)
    return (
        <div className='text-center'>
            <div className="row">
                {productList.map((item, index) => {
                    return <ProductItem product={item} key={index} />
                })}
            </div>

        </div>
    )
}
