import React, { useEffect, useState } from 'react'
import ProductItem from '../ProductItem'
import { useDispatch, useSelector } from 'react-redux'
import { RootState } from '../../../../store'
import { Product } from '../../../../types/product.type'
import axios from 'axios'
import { getProductList } from '../../../../product.reducer'
import $ from 'jquery'

export default function ProductList() {
    const dispatch = useDispatch();
    const productList = useSelector((state: RootState) => state.productReducer.productList);
    const [page, setPage] = useState(1);
    const getProductionList = async () => {
        const offset = (page - 1) * 20;
        const res = await axios.get(`https://dummyjson.com/products?limit=20&offset=${offset}`)
            // console.log(res)
            if(res.status===200){
                setPage(page + 1);
                res.data.products.map((item:Product,index:RootState)=>{
                    return dispatch(getProductList(item))
                })
            }
    }
    useEffect(()=>{
        getProductionList();
        $(window).scroll(function() {
            const a: any = $(window).scrollTop();
                const b: any = $(window).height();
                if(a + b === $(document).height()) {
                    getProductionList();
                }
        });
    },[])


    return (
        <div className='text-center'>
            <div className="row">
                {productList.map((item, index) => {
                    return <ProductItem product={item} key={index} />
                })}
            </div>

        </div>
    )
}
